#include "tester.h"

int     main(int ac, char **av)
{
    system("clear");
    printf ("## -------> TESTS STRLEN\n## \033[0;33m---------------------\033[4;37m\n## | strlen            | ft_strlen         \033[0m| Results :\n");
    _TEST_STRLEN_UNIT("hello");
    _TEST_STRLEN_UNIT("hello world");
    _TEST_STRLEN_UNIT("\\\\\\\\\\");
    _TEST_STRLEN_UNIT(NULL);
    _TEST_STRLEN_UNIT((char *)NULL);
    _TEST_STRLEN_UNIT("");
    _TEST_STRLEN_UNIT("\033[1;1\\n\e\e\t");

    printf ("\n## -------> TESTS STRCMP\n## \033[0;33m---------------------\033[4;37m\n## | strcmp            | ft_strcmp         \033[0m| Results :\n");
    _TEST_STRCMP_UNIT("hello", "hello");
    _TEST_STRCMP_UNIT("hello", "world");
    _TEST_STRCMP_UNIT("hello", "");
    _TEST_STRCMP_UNIT("", "world");
    _TEST_STRCMP_UNIT(NULL, "world");
    _TEST_STRCMP_UNIT("hello", NULL);
    _TEST_STRCMP_UNIT(NULL, NULL);

    printf ("\n## -------> TESTS STRCPY\n## \033[0;33m---------------------\033[4;37m\n## | strcpy            | ft_strcpy         \033[0m| Results :\n");
    _TEST_STRCPY_UNIT("hello", "world", 5, 5);
    _TEST_STRCPY_UNIT("hello", "", 5, 5);
    _TEST_STRCPY_UNIT("", "world", 5, 5);
    _TEST_STRCPY_UNIT(NULL, "world", 5, 5);
    _TEST_STRCPY_UNIT("hello", NULL, 5, 5);
    _TEST_STRCPY_UNIT(NULL, NULL, 0, 0);

    printf ("\n## ------> TESTS STRNCPY\n## \033[0;33m---------------------\033[4;37m\n## | strncpy           | ft_strncpy        \033[0m| Results :\n");
    _TEST_STRNCPY_UNIT("hello", "world", 5, 5, 5);
    _TEST_STRNCPY_UNIT("hello", "", 3, 5, 5);
    _TEST_STRNCPY_UNIT("", "world", 6, 5, 5);
    _TEST_STRNCPY_UNIT(NULL, "world", 2, 5, 5);
    _TEST_STRNCPY_UNIT("hello", NULL, 8, 5, 5);
    _TEST_STRNCPY_UNIT(NULL, NULL, 3, 0, 0);

    printf ("\n## -------> TESTS STRDUP\n## \033[0;33m---------------------\033[4;37m\n## | strdup            | ft_strdup         \033[0m| Results :\n");
    _TEST_STRDUP_UNIT("hello");
    _TEST_STRDUP_UNIT("hello world");
    _TEST_STRDUP_UNIT("\\\\\\\\\\");
    _TEST_STRDUP_UNIT(NULL);
    _TEST_STRDUP_UNIT((char *)NULL);
    _TEST_STRDUP_UNIT("");
    _TEST_STRDUP_UNIT("\033[1;1\\n\e\e\t");

    return (0);
}
